mapper.select:
SELECT SUM(RUNNING) AS RUNNING, SUM(BACKUP) AS BACKUP, SUM(STOPPED) AS STOPPED, SUM(UNKNOWN) AS UNKNOWN FROM(
    SELECT COUNT(PT.PLAN_TRAIN_ID) AS RUNNING, 0 AS BACKUP, 0 AS STOPPED, 0 AS UNKNOWN FROM PLAN_TRAIN PT WHERE PT.SPARE_FLAG = 1 AND PT.RUN_DATE = #{date}
    UNION
    SELECT 0 AS RUNNING, COUNT(PT.PLAN_TRAIN_ID) AS BACKUP, 0 AS STOPPED, 0 AS UNKNOWN FROM PLAN_TRAIN PT WHERE PT.SPARE_FLAG = 2 AND PT.RUN_DATE = #{date}
    UNION
    SELECT 0 AS RUNNING, 0 AS BACKUP, COUNT(PT.PLAN_TRAIN_ID) AS STOPPED, 0 AS UNKNOWN FROM PLAN_TRAIN PT WHERE PT.SPARE_FLAG = 0 AND PT.RUN_DATE = #{date}
    UNION
    SELECT 0 AS RUNNING, 0 AS BACKUP, 0 AS STOPPED, COUNT(PT.PLAN_TRAIN_ID) AS UNKNOWN FROM PLAN_TRAIN PT WHERE ((PT.SPARE_FLAG <> 0 AND PT.SPARE_FLAG <> 1 AND PT.SPARE_FLAG <> 2) OR PT.SPARE_FLAG IS NULL) AND PT.RUN_DATE = #{date}
)

SELECT SUM(LINE) AS LINE, SUM(PLAN) AS PLAN, SUM(UNKNOWN) AS UNKNOWN FROM(
    SELECT COUNT(PT.PLAN_TRAIN_ID) AS LINE, 0 AS PLAN, 0 AS UNKNOWN FROM PLAN_TRAIN PT WHERE PT.DAILYPLAN_FLAG = 0 AND PT.RUN_DATE = #{date}
    UNION
    SELECT 0 AS LINE, COUNT(PT.PLAN_TRAIN_ID) AS PLAN, 0 AS UNKNOWN FROM PLAN_TRAIN PT WHERE PT.DAILYPLAN_FLAG = 1 AND PT.RUN_DATE = #{date}
    UNION
    SELECT 0 AS LINE, 0 AS PLAN, COUNT(PT.PLAN_TRAIN_ID) AS UNKNOWN FROM PLAN_TRAIN PT WHERE ((PT.DAILYPLAN_FLAG <> 1 AND PT.DAILYPLAN_FLAG <> 0) OR PT.DAILYPLAN_FLAG IS NULL) AND PT.RUN_DATE = #{date}
)
