SELECT P.* FROM ( SELECT M.*, ROWNUM NUM FROM (
SELECT PLAN_TRAIN_ID AS planTrainId, PLAN_TRAIN_SIGN AS planTrainSign, PLAN_CROSS_ID AS planCrossId, MARSHALLING_NAME AS marshallingName, GROUP_SERIAL_NBR AS groupSerialNbr, TRAIN_SORT AS trainSort, PRE_TRAIN_ID AS preTrainId, NEXT_TRAIN_ID AS nextTrainId, RUN_DATE AS runDate, TRAIN_NBR AS trainNbr, to_char(START_TIME,'yyyy-MM-dd hh24:mi:SS') AS startDateTime, to_char(END_TIME,'yyyy-MM-dd hh24:mi:SS') AS endDateTime, START_BUREAU AS startBureauShortName, START_BUREAU_FULL AS startBureauFullName, START_STN AS startStn, END_BUREAU AS endBureauShortName, END_BUREAU_FULL AS endBureauFullName, END_STN AS endStn, PASS_BUREAU AS passBureau, TRAIN_SCOPE AS trainScope, TRAIN_TYPE_ID AS trainTypeId, HIGHLINE_FLAG AS highLineFlag, BASE_CHART_ID AS baseChartId, BASE_TRAIN_ID AS baseTrainId, HIGHLINE_RULE AS hightLineRule, COMMONLINE_RULE AS commonLineRule, APPOINT_WEEK AS appointWeek, APPOINT_DAY AS appointDay, DAY_GAP AS dayGap, SPARE_FLAG AS spareFlag, SPARE_APPLY_FLAG AS spareApplyFlag, CREAT_TYPE AS createType, CREAT_TIME AS createDateTime, CHECK_LEV1_TYPE AS checkLev1Type, CHECK_LEV2_TYPE AS checkLev2Type, DAILYPLAN_TIMES AS dailyPlanTimes, DAILYPLAN_FLAG AS dailyPlanFlag
FROM PLAN_TRAIN WHERE 1=1 AND RUN_DATE=#{runDate}
AND TRAIN_NBR=#{trainNbr}
)M ) P WHERE P.NUM>=${rownumstart} AND P.NUM <=${rownumend}

    	SELECT COUNT(PLAN_TRAIN_ID) FROM PLAN_TRAIN 
    	WHERE 1=1 AND RUN_DATE = #{runDate}

			AND TRAIN_NBR=#{trainNbr}

SELECT P.* FROM ( SELECT M.*, ROWNUM NUM FROM (
SELECT HIGHLINE_CREW_ID AS crewHighlineId, CREW_DATE AS crewDate, CREW_BUREAU AS crewBureau, CREW_TYPE AS crewType, CREW_CROSS AS crewCross, CREW_GROUP AS crewGroup, THROUGH_LINE AS throughLine, NAME1 AS name1, TEL1 AS tel1, IDENTITY1 AS identity1, NAME2 AS name2, TEL2 AS tel2, IDENTITY2 AS identity2, NOTE AS note, RECORD_PEOPLE AS recordPeople, RECORD_PEOPLE_ORG AS recordPeopleOrg, RECORD_TIME AS recordTime, SUBMIT_TYPE AS submitType
FROM HIGHLINE_CREW WHERE CREW_DATE=#{crewDate} AND CREW_TYPE=#{crewType}
)M ) P WHERE P.NUM>=${rownumstart} AND P.NUM <=${rownumend}
