mapper.select:
SELECT t.HIGHLINE_CROSS_ID AS highlineCrossId, t.PLAN_CROSS_ID AS planCrossId, t.CROSS_START_DATE AS crossStartDate, t.CROSS_END_DATE AS crossEndDate, t.CROSS_START_STN AS crossStartStn, t.CROSS_END_STN AS crossEndStn, t.CROSS_NAME AS crossName, t.SPARE_FLAG AS spareFlag, t.RELEVANT_BUREAU AS relevantBureau, t.TOKEN_VEH_BUREAU AS tokenVehBureau, t.TOKEN_VEH_DEPT AS tokenVehDept, t.TOKEN_VEH_DEPOT AS tokenVehDepot, t.TOKEN_PSG_BUREAU AS tokenPsgBureau , t.TOKEN_PSG_DEPT AS tokenPsgDept, t.CRH_TYPE AS crhType, t.VEHICLE1 AS vehicle1, t.VEHICLE2 AS vehicle2, t.NOTE AS note, t.CREAT_PEOPLE AS creatPeople, t.CREAT_PEOPLE_ORG AS creatPeopleOrg , t.CREAT_TIME AS creatTime, t.THROUGH_LINE AS throughLine, t.POST_ID AS postId, t.POST_Name AS postName, t.CROSS_BUREAU AS crossBureau, t.CREAT_REASON AS createReason, t.VEHICLE_SUB_TYPE AS vehicleSubType, t.VEHICLE_SUB_PEOPLE AS vehicleSubPeople, t.VEHICLE_SUB_PEOPLE_ORG AS vehicleSubPeopleOrg, TO_CHAR(t.VEHICLE_SUB_TIME,'yyyy-MM-dd hh24:mi:SS') AS vehicleSubTime, t.VEHICLE_CHECK_TYPE AS vehicleCheckType, t.VEHICLE_CHECK_PEOPLE AS vehicleCheckPeople, t.VEHICLE_CHECK_PEOPLE_ORG AS vehicleCheckPeopleOrg, TO_CHAR(t.VEHICLE_CHECK_TIME,'yyyy-MM-dd hh24:mi:SS') AS vehicleCheckTime, t.CROSS_CHECK_TYPE AS crossCheckType, t.CROSS_CHECK_PEOPLE AS crossCheckPeople, t.CROSS_CHECK_PEOPLE_ORG AS crossCheckPeopleOrg, TO_CHAR(t.CROSS_CHECK_TIME,'yyyy-MM-dd hh24:mi:SS') AS crossCheckTime FROM HIGHLINE_CROSS T WHERE 1=1
AND T.CROSS_BUREAU = #{crossBureau}
AND T.CROSS_START_DATE = #{crossStartDate}
ORDER BY t.TOKEN_VEH_BUREAU,t.CROSS_NAME

SELECT t.HIGHLINE_CROSS_ID AS highlineCrossId, t.PLAN_CROSS_ID AS planCrossId, t.CROSS_START_DATE AS crossStartDate, t.CROSS_END_DATE AS crossEndDate, t.CROSS_START_STN AS crossStartStn, t.CROSS_END_STN AS crossEndStn, t.CROSS_NAME AS crossName, t.SPARE_FLAG AS spareFlag, t.RELEVANT_BUREAU AS relevantBureau, t.TOKEN_VEH_BUREAU AS tokenVehBureau, t.TOKEN_VEH_DEPT AS tokenVehDept, t.TOKEN_VEH_DEPOT AS tokenVehDepot, t.TOKEN_PSG_BUREAU AS tokenPsgBureau , t.TOKEN_PSG_DEPT AS tokenPsgDept, t.CRH_TYPE AS crhType, t.VEHICLE1 AS vehicle1, t.VEHICLE2 AS vehicle2, t.NOTE AS note, t.CREAT_PEOPLE AS creatPeople, t.CREAT_PEOPLE_ORG AS creatPeopleOrg , t.CREAT_TIME AS creatTime, t.THROUGH_LINE AS throughLine, t.POST_ID AS postId, t.POST_Name AS postName, t.CROSS_BUREAU AS crossBureau, t.CREAT_REASON AS createReason, t.VEHICLE_SUB_TYPE AS vehicleSubType, t.VEHICLE_SUB_PEOPLE AS vehicleSubPeople, t.VEHICLE_SUB_PEOPLE_ORG AS vehicleSubPeopleOrg, TO_CHAR(t.VEHICLE_SUB_TIME,'yyyy-MM-dd hh24:mi:SS') AS vehicleSubTime, t.VEHICLE_CHECK_TYPE AS vehicleCheckType, t.VEHICLE_CHECK_PEOPLE AS vehicleCheckPeople, t.VEHICLE_CHECK_PEOPLE_ORG AS vehicleCheckPeopleOrg, TO_CHAR(t.VEHICLE_CHECK_TIME,'yyyy-MM-dd hh24:mi:SS') AS vehicleCheckTime, t.CROSS_CHECK_TYPE AS crossCheckType, t.CROSS_CHECK_PEOPLE AS crossCheckPeople, t.CROSS_CHECK_PEOPLE_ORG AS crossCheckPeopleOrg, TO_CHAR(t.CROSS_CHECK_TIME,'yyyy-MM-dd hh24:mi:SS') AS crossCheckTime FROM HIGHLINE_CROSS T WHERE 1=1
AND T.CROSS_BUREAU = #{crossBureau}
AND T.CROSS_START_DATE = #{crossStartDate}

SELECT t.HIGHLINE_CROSS_ID AS highlineCrossId, t.PLAN_CROSS_ID AS planCrossId, t.CROSS_START_DATE AS crossStartDate, t.CROSS_END_DATE AS crossEndDate, t.CROSS_START_STN AS crossStartStn, t.CROSS_END_STN AS crossEndStn, t.CROSS_NAME AS crossName, t.SPARE_FLAG AS spareFlag, t.RELEVANT_BUREAU AS relevantBureau, t.TOKEN_VEH_BUREAU AS tokenVehBureau, t.TOKEN_VEH_DEPT AS tokenVehDept, t.TOKEN_VEH_DEPOT AS tokenVehDepot, t.TOKEN_PSG_BUREAU AS tokenPsgBureau , t.TOKEN_PSG_DEPT AS tokenPsgDept, t.CRH_TYPE AS crhType, t.VEHICLE1 AS vehicle1, t.VEHICLE2 AS vehicle2, t.NOTE AS note, t.CROSS_DATE AS crossDate, t.CREAT_PEOPLE AS creatPeople, t.CREAT_PEOPLE_ORG AS creatPeopleOrg , t.CREAT_TIME AS creatTime, t.THROUGH_LINE AS throughLine, t.POST_ID AS postId, t.POST_Name AS postName, t.CROSS_BUREAU AS crossBureau, t.CREAT_REASON AS createReason, t.VEHICLE_SUB_TYPE AS vehicleSubType, t.VEHICLE_SUB_PEOPLE AS vehicleSubPeople, t.VEHICLE_SUB_PEOPLE_ORG AS vehicleSubPeopleOrg, TO_CHAR(t.VEHICLE_SUB_TIME,'yyyy-MM-dd hh24:mi:SS') AS vehicleSubTime, t.VEHICLE_CHECK_TYPE AS vehicleCheckType, t.VEHICLE_CHECK_PEOPLE AS vehicleCheckPeople, t.VEHICLE_CHECK_PEOPLE_ORG AS vehicleCheckPeopleOrg, TO_CHAR(t.VEHICLE_CHECK_TIME,'yyyy-MM-dd hh24:mi:SS') AS vehicleCheckTime, t.CROSS_CHECK_TYPE AS crossCheckType, t.CROSS_CHECK_PEOPLE AS crossCheckPeople, t.CROSS_CHECK_PEOPLE_ORG AS crossCheckPeopleOrg, TO_CHAR(t.CROSS_CHECK_TIME,'yyyy-MM-dd hh24:mi:SS') AS crossCheckTime FROM HIGHLINE_CROSS t LEFT JOIN dic_rela_cross_post b ON t.base_cross_id=b.base_cross_id WHERE t.CROSS_DATE = #{crossDate}
and t.TOKEN_VEH_BUREAU=#{tokenVehBureau}
and t.CROSS_NAME like '%' || ${trainNbr} || '%'
and t.THROUGH_LINE=#{throughLine}
and t.TOKEN_VEH_DEPOT=#{tokenVehDepot}
and t.cross_bureau = #{crossBureau}
and t.POST_NAME = #{postName}
and t.CROSS_CHECK_TYPE = ${crossCheckType}
and t.VEHICLE_SUB_TYPE = ${vehicleSubType}
and t.VEHICLE_CHECK_TYPE = ${vehicleCheckType}
ORDER BY t.CROSS_NAME

mapper.update:
UPDATE HIGHLINE_CROSS SET
VEHICLE1 = '', VEHICLE1 = #{vehicle1},
VEHICLE2 = '' VEHICLE2 = #{vehicle2}
WHERE HIGHLINE_CROSS_ID = #{highlineCrossId}

UPDATE HIGHLINE_CROSS_TRAIN 
  SET HIGHLINE_CROSS_ID=#{highLineCrossId} 
  WHERE HIGHLINE_TRAIN_ID=#{highLineTrainId}

mapper.select:
select t3.ljpym as bureauCode, t2.acc_id as code,T2.ACC_NAME as name from tds1adm.ACCOUNT t2,tds1adm.department t3 where t2.acc_id in (select t1.acc_id from tds1adm.ACC_ROL_RELA t1 where t1.rol_id in (select t.rol_id from tds1adm.ROLE t where t.rol_name like '局动车调度')) and t2.fenlei01 = t3.dep_id
and t3.ljpym=#{bureauCode}
order by t2.acc_id
