mapper:
SELECT * FROM ( SELECT TLS.CHILD_INDEX AS STN_INDEX, TLS.NAME AS STN_NAME, TLS.BUREAU_NAME AS BUREAU, TLS.TRACK_NAME AS TRACK_NAME, '' AS ARR_TIME, TO_CHAR(TLS.TIME, 'yyyy-mm-dd hh24:mi') AS DPT_TIME FROM JHPT_RJH.M_TRAINLINE_SCHEDULE_SITEM TLS WHERE TLS.PARENT_ID = #{lineId} UNION SELECT TLS.CHILD_INDEX AS STN_INDEX, TLS.NAME AS STN_NAME, TLS.BUREAU_NAME AS BUREAU, TLS.TRACK_NAME AS TRACK_NAME, TO_CHAR(TLS.SOURCE_TIME, 'yyyy-mm-dd hh24:mi') AS ARR_TIME, TO_CHAR(TLS.TARGET_TIME, 'yyyy-mm-dd hh24:mi') AS DPT_TIME FROM JHPT_RJH.M_TRAINLINE_SCHEDULE_RITEM TLS WHERE TLS.PARENT_ID = #{lineId} UNION SELECT TLS.CHILD_INDEX AS STN_INDEX, TLS.NAME AS STN_NAME, TLS.BUREAU_NAME AS BUREAU, TLS.TRACK_NAME AS TRACK_NAME, TO_CHAR(TLS.TIME, 'yyyy-mm-dd hh24:mi') AS ARR_TIME, '' AS DPT_TIME FROM JHPT_RJH.M_TRAINLINE_SCHEDULE_TITEM TLS WHERE TLS.PARENT_ID = #{lineId} ORDER BY STN_INDEX )
SELECT CASE WHEN PT.TRAIN_NBR = TL.NAME THEN 1 ELSE 0 END AS TRAIN_NAME, CASE WHEN PT.START_BUREAU_FULL = TL.SOURCE_BUREAU_NAME THEN 1 ELSE 0 END AS START_BUREAU, CASE WHEN PT.END_BUREAU_FULL = TL.TARGET_BUREAU_NAME THEN 1 ELSE 0 END AS END_BUREAU, CASE WHEN PT.START_STN = TL.SOURCE_NODE_NAME THEN 1 ELSE 0 END AS START_STN, CASE WHEN PT.END_STN = TL.TARGET_NODE_NAME THEN 1 ELSE 0 END AS END_STN, CASE WHEN TO_CHAR(PT.START_TIME, 'yyyy-mm-dd hh24:mi') = TO_CHAR(TL.SOURCE_TIME, 'yyyy-mm-dd hh24:mi') THEN 1 ELSE 0 END AS START_TIME, CASE WHEN TO_CHAR(PT.END_TIME, 'yyyy-mm-dd hh24:mi') = TO_CHAR(TL.TARGET_TIME, 'yyyy-mm-dd hh24:mi') THEN 1 ELSE 0 END AS END_TIME FROM PLAN_TRAIN PT LEFT JOIN JHPT_RJH.M_TRAINLINE TL ON PT.DAILYPLAN_ID = TL.ID WHERE PT.PLAN_TRAIN_ID = '${planId}' AND TL.ID = '${lineId}'
SELECT TL.NAME AS TRAIN_NAME, TL.SOURCE_BUREAU_NAME AS START_BUREAU, TL.TARGET_BUREAU_NAME AS END_BUREAU, TL.SOURCE_NODE_NAME AS START_STN, TL.TARGET_NODE_NAME AS END_STN, TO_CHAR(TL.SOURCE_TIME, 'yyyy-mm-dd hh24:mi') AS START_TIME, TO_CHAR(TL.TARGET_TIME, 'yyyy-mm-dd hh24:mi') AS END_TIME FROM JHPT_RJH.M_TRAINLINE TL WHERE TL.ID = #{lineId}
