mapper.select:
  select count(*) as count from jhpt_rjh.m_trainlinetemp t where 1=1  AND t.scheme_id = #{chartId}

		AND t.name like '%' || #{trainNbr} || '%'


	   AND t.source_bureau_shortname = #{startBureauShortName}


	   AND t.target_bureau_shortname = #{endBureauShortName}

select * from (select ROWNUM AS num, m.* from ( select id as planTrainId,name as trainNbr, source_node_name as startStn, target_node_name as endStn, route_bureau_shortnames as routingBureauShortName, relative_target_time_day as relativeTargetTimeDay, source_bureau_shortname as startBureau, target_bureau_shortname as endBureau, source_bureau_name as startBureauFull, target_bureau_name as endBureauFull, source_time as startTimeStr, target_time as endTimeStr from jhpt_rjh.m_trainlinetemp t where 1=1 AND t.scheme_id = #{chartId}
AND t.name like '%' || #{trainNbr} || '%'
AND t.source_bureau_shortname = #{startBureauShortName}
AND t.target_bureau_shortname = #{endBureauShortName}
order by trainNbr ) m ) where num >=#{rownumstart} AND num <=#{rownumend}
