mapper.select:
  select count(*) as count from jhpt_rjh.m_trainlinetemp t where 1=1  AND t.scheme_id = #{chartId}

		AND t.name like '%' || #{trainNbr} || '%'


	   AND t.source_bureau_shortname = #{startBureauShortName}


	   AND t.target_bureau_shortname = #{endBureauShortName}

select id as planTrainId, name as trainNbr, source_node_name as startStn, target_node_name as endStn, source_bureau_shortname as startBureauShortName, target_bureau_shortname as endBureau, route_bureau_shortnames as routingBureauShortName, relative_target_time_day as relativeTargetTimeDay, source_bureau_name as startBureauFull, target_bureau_name as endBureauFull, to_char(source_time,'hh24:mi:ss') as startTimeStr, to_char(target_time,'hh24:mi:ss') as endTimeStr, operation as operation from jhpt_rjh.m_trainlinetemp t where 1=1 AND t.scheme_id = #{chartId}
AND t.name= #{trainNbr}
AND t.operation= #{operation}
AND t.source_bureau_shortname = #{startBureauShortName}
AND t.target_bureau_shortname = #{endBureauShortName}
order by trainNbr

select * from (select ROWNUM AS num, m.* from ( select id as planTrainId, name as trainNbr, source_node_name as startStn, target_node_name as endStn, source_bureau_shortname as startBureau, target_bureau_shortname as endBureau, route_bureau_shortnames as routingBureauShortName, relative_target_time_day as relativeTargetTimeDay, source_bureau_name as startBureauFull, target_bureau_name as endBureauFull, to_char(source_time,'hh24:mi:ss') as startTimeStr, to_char(target_time,'hh24:mi:ss') as endTimeStr, operation as operation from jhpt_rjh.m_trainlinetemp t where 1=1 AND t.scheme_id = #{chartId}
AND t.name like '%' || #{trainNbr} || '%'
AND t.source_bureau_shortname = #{startBureauShortName}
AND t.target_bureau_shortname = #{endBureauShortName}
order by trainNbr ) m ) where num >=#{rownumstart} AND num <=#{rownumend}
